INSERT INTO `config_info` VALUES (1, 'snowy-actuator-app-dev.yaml', 'dev', '# 服务配置\r\nserver:\r\n  port: 9001\r\n\r\n# spring相关配置\r\nspring:\r\n  boot:\r\n    admin:\r\n      client:\r\n        url: http://localhost:9001\r\n        instance:\r\n          # 默认使用的是主机名注册，改为使用ip注册\r\n          prefer-ip: true\r\n  cloud:\r\n    # nacos配置\r\n    nacos:\r\n      discovery:\r\n        server-addr: ${spring.cloud.nacos.config.server-addr}\r\n        namespace: ${spring.cloud.nacos.config.namespace}\r\n        group: ${spring.cloud.nacos.config.group}\r\n        username: ${spring.cloud.nacos.config.username}\r\n        password: ${spring.cloud.nacos.config.password}\r\n\r\n# 开放所有endpoint，实际生产根据自身需要开放，出于安全考虑不建议全部开放\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \"*\"\r\n    health:\r\n      show-details: \"ALWAYS\"', '62b64acc7e8916c19b2432b6ef0ad089', '2024-04-24 14:33:07', '2024-04-24 14:33:07', 'nacos', '192.168.0.105', '', 'snowy-cloud-dev', NULL, NULL, NULL, 'yaml', NULL, '');
INSERT INTO `config_info` VALUES (2, 'snowy-sentinel-app-dev.yaml', 'dev', '# 服务配置\r\nserver:\r\n  port: 9002\r\n  servlet:\r\n    #spring settings\r\n    encoding:\r\n      force: true\r\n      charset: UTF-8\r\n      enabled: true\r\n    #cookie name setting\r\n    session:\r\n      cookie:\r\n        name:\r\n          sentinel_dashboard_cookie: sentinel_dashboard_cookie\r\n\r\n# spring相关配置\r\nspring:\r\n  mvc:\r\n    pathmatch:\r\n      matching-strategy: ant_path_matcher\r\n  boot:\r\n    admin:\r\n      client:\r\n        url: http://localhost:9001\r\n        instance:\r\n          # 默认使用的是主机名注册，改为使用ip注册\r\n          prefer-ip: true\r\n  cloud:\r\n    # nacos配置\r\n    nacos:\r\n      discovery:\r\n        server-addr: ${spring.cloud.nacos.config.server-addr}\r\n        namespace: ${spring.cloud.nacos.config.namespace}\r\n        group: ${spring.cloud.nacos.config.group}\r\n        username: ${spring.cloud.nacos.config.username}\r\n        password: ${spring.cloud.nacos.config.password}\r\n\r\n# 开放所有endpoint，实际生产根据自身需要开放，出于安全考虑不建议全部开放\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \"*\"\r\n    health:\r\n      show-details: \"ALWAYS\"\r\n\r\n# logging settings\r\nlogging:\r\n  level:\r\n    org:\r\n      springframework:\r\n        web: INFO\r\n  file:\r\n    name: ${user.home}/logs/csp/sentinel-dashboard.log\r\n  pattern:\r\n    file: \'%d{yyyy-MM-dd HH:mm:ss} [%thread] %-5level %logger{36} - %msg%n\'\r\n    console: \'%d{yyyy-MM-dd HH:mm:ss} [%thread] %-5level %logger{36} - %msg%n\'\r\n\r\n# auth settings\r\nauth:\r\n  filter:\r\n    exclude-urls: /,/auth/login,/auth/logout,/registry/machine,/version,/actuator/**,/details\r\n    exclude-url-suffixes: htm,html,js,css,map,ico,ttf,woff,png\r\n  # If auth.enabled=false, Sentinel console disable login\r\n  username: sentinel\r\n  password: sentinel\r\n\r\n# Inject the dashboard version. It\'s required to enable\r\n# filtering in pom.xml for this resource file.\r\nsentinel:\r\n  dashboard:\r\n    version: 1.8.7', 'b87739eb375a52be36b6b6f125d231c2', '2024-04-24 14:33:29', '2024-04-24 14:33:29', 'nacos', '192.168.0.105', '', 'snowy-cloud-dev', NULL, NULL, NULL, 'yaml', NULL, '');
INSERT INTO `config_info` VALUES (3, 'snowy-gateway-app-dev.yaml', 'dev', '# 服务配置\nserver:\n  port: 9003\n\n# spring相关配置\nspring:\n  datasource:\n    driver-class-name: com.mysql.cj.jdbc.Driver\n    url: jdbc:mysql://localhost:3306/snowy-cloud?useUnicode=true&characterEncoding=utf-8&useSSL=false&allowPublicKeyRetrieval=true&nullCatalogMeansCurrent=true&useInformationSchema=true\n    username: root\n    password: 123456\n\n# Oracle数据库\n#spring:\n#  datasource:\n#    driver-class-name: oracle.jdbc.OracleDriver\n#    url: jdbc:oracle:thin:@localhost:1521:xe\n#    username: SNOWY-CLOUD-ORACLE\n#    password: 123456\n\n# SQLServer配置\n#spring:\n#  datasource:\n#    driverClassName: com.microsoft.sqlserver.jdbc.SQLServerDriver\n#    url: jdbc:sqlserver://localhost:1433;DatabaseName=snowy-cloud-mssql\n#    username: sa\n#    password: 123456\n\n# PostgreSQL配置\n#spring:\n#  datasource:\n#    driverClassName: org.postgresql.Driver\n#    url: jdbc:postgresql://127.0.0.1:5432/snowy-cloud-postgresql\n#    username: postgres\n#    password: 123456\n\n# 达梦数据库配置\n#spring:\n#  datasource:\n#    driver-class-name: dm.jdbc.driver.DmDriver\n#    url: jdbc:dm://localhost:5236/SYSDBA\n#    username: SYSDBA\n#    password: 123456789\n\n# 人大金仓数据库\n#spring:\n#  datasource:\n#    driver-class-name: com.kingbase8.Driver\n#    url: jdbc:kingbase8://localhost:54321/snowy-cloud-kingbase\n#    username: system\n#    password: 123456\n# #人大金仓数据库兼容问题，不需要加在sql语句中加public的解决方法：\n# #在根目录data下的kingbase.conf文档里面找到search_path = \'\"$user\",PUBLIC,sys_catalog\'进行替换放开\n# #重启数据库即可完全兼容，注意 sql中不能出现mysql中的关键字的单引号\n\n  # redis相关配置\n  data:\n    redis:\n      database: 1\n      host: 127.0.0.1\n      port: 6379\n      password: \n      timeout: 10s\n      lettuce:\n        pool:\n          max-active: 200\n          max-wait: -1ms\n          max-idle: 10\n          min-idle: 0\n\n  boot:\n    admin:\n      client:\n        url: http://localhost:9001\n        instance:\n          # 默认使用的是主机名注册，改为使用ip注册\n          prefer-ip: true\n  cloud:\n    loadbalancer:\n      ribbon:\n        enabled: false\n      cache:\n        enabled: true\n        caffeine:\n          spec: initialCapacity=500,expireAfterWrite=5s\n    # nacos配置\n    nacos:\n      discovery:\n        server-addr: ${spring.cloud.nacos.config.server-addr}\n        namespace: ${spring.cloud.nacos.config.namespace}\n        group: ${spring.cloud.nacos.config.group}\n        username: ${spring.cloud.nacos.config.username}\n        password: ${spring.cloud.nacos.config.password}\n    sentinel:\n      transport:\n        dashboard: localhost:9002\n        port: 8719\n      scg:\n        fallback:\n          mode: response\n          response-status: 455\n          response-body: error!\n    # gateway配置\n    gateway:\n      default-filters:\n        - DedupeResponseHeader=Access-Control-Allow-Origin\n      globalcors:\n        add-to-simple-url-handler-mapping: true\n        corsConfigurations:\n          \'[/**]\':\n            allowed-origins-patterns: \"*\"\n            allowed-methods: \"*\"\n            allowed-headers: \"*\"\n            allow-credentials: true\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        - id: snowy-web-app\n          uri: lb://snowy-web-app\n          predicates:\n            - Path=/api/webapp/**\n          filters:\n            - SwaggerHeaderFilter\n            - StripPrefix=2 #去掉Path中前两个节点\n            - name: Retry\n              args:\n                retries: 1 #重试次数\n        - id: snowy-web-app\n          uri: lb://snowy-web-app\n          predicates:\n            - Path=/druid/**\n          filters:\n            - SwaggerHeaderFilter\n            - name: Retry\n              args:\n                retries: 1 #重试次数\n        - id: snowy-biz-app\n          uri: lb://snowy-biz-app\n          predicates:\n            - Path=/api/bizapp/**\n          filters:\n            - SwaggerHeaderFilter\n            - StripPrefix=2 #去掉Path中前两个节点\n            - name: Retry\n              args:\n                retries: 1 #重试次数\n\n# 开放所有endpoint，实际生产根据自身需要开放，出于安全考虑不建议全部开放\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \"*\"\n    health:\n      show-details: \"ALWAYS\"\n\n#########################################\n# sa-token configuration\n#########################################\nsa-token:\n  token-name: token\n  timeout: 2592000\n  active-timeout: -1\n  is-concurrent: true\n  is-share: false\n  max-login-count: -1\n  token-style: random-32\n  is-log: false\n  is-print: false\n\n  # sa-token alone-redis configuration\n  alone-redis:\n    database: 2\n    host: ${spring.data.redis.host}\n    port: ${spring.data.redis.port}\n    password: ${spring.data.redis.password}\n    timeout: ${spring.data.redis.timeout}\n    lettuce:\n      pool:\n        max-active: ${spring.data.redis.lettuce.pool.max-active}\n        max-wait: ${spring.data.redis.lettuce.pool.max-wait}\n        max-idle: ${spring.data.redis.lettuce.pool.max-idle}\n        min-idle: ${spring.data.redis.lettuce.pool.min-idle}\n\n# knife4j 文档聚合\nknife4j:\n  enable: true\n  production: false\n  basic:\n    enable: true\n    username: admin\n    password: 123456\n  setting:\n    enableOpenApi: false\n    enableSwaggerModels: false\n    enableFooter: false\n    enableFooterCustom: true\n    footerCustomContent: Apache License 2.0 | Copyright 2022-[SNOWY](https://www.xiaonuo.vip)\n  # gateway聚合swagger文档\n  gateway:\n    enabled: true\n    # 排序规则(tag/operation排序自4.2.0版本新增)\n    # 取值：alpha-默认排序规则，官方swagger-ui默认实现,order-Knife4j提供的增强排序规则，开发者可扩展x-order，根据数值来自定义排序\n    tags-sorter: order\n    operations-sorter: order\n     # 指定手动配置的模式(默认为该模式)\n    strategy: manual\n    routes:\n      - name: SNOWY-PLUGIN-AUTH\n        url: /api/webapp/v3/api-docs/SNOWY-PLUGIN-AUTH\n        service-name: snowy-web-app\n        context-path: /\n      - name: SNOWY-PLUGIN-BIZ\n        url: /api/bizapp/v3/api-docs/SNOWY-PLUGIN-BIZ\n        service-name: snowy-biz-app\n        context-path: /\n      - name: SNOWY-PLUGIN-CLIENT\n        url: /api/bizapp/v3/api-docs/SNOWY-PLUGIN-CLIENT\n        service-name: snowy-biz-app\n        context-path: /\n      - name: SNOWY-PLUGIN-DEV\n        url: /api/webapp/v3/api-docs/SNOWY-PLUGIN-DEV\n        service-name: snowy-web-app\n        context-path: /\n      - name: SNOWY-PLUGIN-GEN\n        url: /api/webapp/v3/api-docs/SNOWY-PLUGIN-GEN\n        service-name: snowy-web-app\n        context-path: /\n      - name: SNOWY-PLUGIN-MOBILE\n        url: /api/webapp/v3/api-docs/SNOWY-PLUGIN-MOBILE\n        service-name: snowy-web-app\n        context-path: /\n      - name: SNOWY-PLUGIN-SYS\n        url: /api/webapp/v3/api-docs/SNOWY-PLUGIN-SYS\n        service-name: snowy-web-app\n        context-path: /', 'f71456503df8b00b1e240fbaf59ba1f6', '2024-04-24 14:34:18', '2024-05-22 15:11:42', 'nacos', '192.168.0.105', '', 'snowy-cloud-dev', '', '', '', 'yaml', '', '');
INSERT INTO `config_info` VALUES (4, 'snowy-xxl-job-app-dev.yaml', 'dev', '# 服务配置\r\nserver:\r\n  port: 9004\r\n  servlet:\r\n    context-path: /xxl-job-admin\r\n\r\n# spring相关配置\r\nspring:\r\n  boot:\r\n    admin:\r\n      client:\r\n        url: http://localhost:9001\r\n        instance:\r\n          # 默认使用的是主机名注册，改为使用ip注册\r\n          prefer-ip: true\r\n  cloud:\r\n    loadbalancer:\r\n      ribbon:\r\n        enabled: false\r\n      cache:\r\n        enabled: true\r\n        caffeine:\r\n          spec: initialCapacity=500,expireAfterWrite=5s\r\n    # nacos配置\r\n    nacos:\r\n      discovery:\r\n        server-addr: ${spring.cloud.nacos.config.server-addr}\r\n        namespace: ${spring.cloud.nacos.config.namespace}\r\n        group: ${spring.cloud.nacos.config.group}\r\n        username: ${spring.cloud.nacos.config.username}\r\n        password: ${spring.cloud.nacos.config.password}\r\n        metadata:\r\n          management.context-path: ${server.servlet.context-path}/actuator\r\n      config:\r\n        server-addr: localhost:8848 #Nacos配置中心地址\r\n        file-extension: yml #指定yml格式的配置\r\n        username: nacos\r\n        password: nacos\r\n  datasource:\r\n    ### xxl-job, datasource\r\n    driver-class-name: com.mysql.cj.jdbc.Driver\r\n    url: jdbc:mysql://localhost:3306/snowy-cloud-job?autoReconnect=true&useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=CONVERT_TO_NULL&useSSL=false&nullCatalogMeansCurrent=true\r\n    username: root\r\n    password: 123456\r\n    ### datasource-pool\r\n    type: com.zaxxer.hikari.HikariDataSource\r\n    hikari:\r\n      minimum-idle: 10\r\n      maximum-pool-size: 30\r\n      auto-commit: true\r\n      idle-timeout: 30000\r\n      pool-name: HikariCP\r\n      max-lifetime: 900000\r\n      connection-timeout: 10000\r\n      connection-test-query: SELECT 1\r\n      validation-timeout: 1000\r\n  ### resources\r\n  resources:\r\n    static-locations: classpath:/static/\r\n  mvc:\r\n    servlet:\r\n      load-on-startup: 0\r\n    static-path-pattern: /static/**\r\n  ### freemarker\r\n  freemarker:\r\n    templateLoaderPath: classpath:/templates/\r\n    suffix: .ftl\r\n    charset: UTF-8\r\n    request-context-attribute: request\r\n    settings:\r\n      number_format: 0.##########\r\n  ### xxl-job, email\r\n  mail:\r\n    host: smtp.qq.com\r\n    port: 25\r\n    username: xxx@qq.com\r\n    from: xxx@qq.com\r\n    password: xxx\r\n    properties:\r\n      mail:\r\n        smtp:\r\n          auth: true\r\n          starttls:\r\n            enable: true\r\n            required: true\r\n          socketFactory:\r\n            class: javax.net.ssl.SSLSocketFactory\r\n\r\n### mybatis\r\nmybatis:\r\n  mapper-locations: classpath:/mybatis-mapper/*Mapper.xml\r\n# type-aliases-package: com.xxl.job.admin.core.model\r\n\r\n### xxl-job\r\n### xxl-job, access token\r\nxxl:\r\n  job:\r\n    accessToken: 123456\r\n    ### xxl-job, i18n (default is zh_CN, and you can choose \"zh_CN\", \"zh_TC\" and \"en\")\r\n    i18n: zh_CN\r\n    ## xxl-job, triggerpool max size\r\n    triggerpool:\r\n      fast:\r\n        max: 200\r\n      slow:\r\n        max: 100\r\n    ### xxl-job, log retention days\r\n    logretentiondays: 30\r\n\r\n### actuator\r\n# 开放所有endpoint，实际生产根据自身需要开放，出于安全考虑不建议全部开放\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \"*\"\r\n    health:\r\n      show-details: \"ALWAYS\"\r\n  health:\r\n    mail:\r\n      enabled: false', '544e57a2a3ce12e2c5f26e865a297f16', '2024-04-24 14:34:37', '2024-04-24 14:34:37', 'nacos', '192.168.0.105', '', 'snowy-cloud-dev', NULL, NULL, NULL, 'yaml', NULL, '');
INSERT INTO `config_info` VALUES (5, 'snowy-web-app-dev.yaml', 'dev', '#########################################\n# server configuration\n#########################################\nserver:\n  port: 9101\n\n#########################################\n# spring profiles configuration\n#########################################\nspring:\n\n  #########################################\n  # multipart configuration\n  #########################################\n  servlet:\n    multipart:\n      max-request-size: 100MB\n      max-file-size: 100MB\n\n  #########################################\n  # mvc configuration\n  #########################################\n  mvc:\n    pathmatch:\n      matching-strategy: ant_path_matcher\n\n  #########################################\n  # main configuration\n  #########################################\n  main:\n    allow-circular-references: true\n\n  #########################################\n  # boot configuration\n  #########################################\n  boot:\n    admin:\n      client:\n        url: http://localhost:9001\n        instance:\n          # 默认使用的是主机名注册，改为使用ip注册\n          prefer-ip: true\n\n  #########################################\n  # cloud configuration\n  #########################################\n  cloud:\n    loadbalancer:\n      ribbon:\n        enabled: false\n      cache:\n        enabled: true\n        caffeine:\n          spec: initialCapacity=500,expireAfterWrite=5s\n    nacos:\n      discovery:\n        server-addr: ${spring.cloud.nacos.config.server-addr}\n        namespace: ${spring.cloud.nacos.config.namespace}\n        group: ${spring.cloud.nacos.config.group}\n        username: ${spring.cloud.nacos.config.username}\n        password: ${spring.cloud.nacos.config.password}\n\n  #########################################\n  # datasource configuration\n  #########################################\n\n  # mysql\n  datasource:\n    dynamic:\n      datasource:\n        master:\n          driver-class-name: com.mysql.cj.jdbc.Driver\n          url: jdbc:mysql://localhost:3306/snowy-cloud?useUnicode=true&characterEncoding=utf-8&useSSL=false&allowPublicKeyRetrieval=true&nullCatalogMeansCurrent=true&useInformationSchema=true\n          username: root\n          password: 123456\n      strict: true\n\n  # oracle\n#  datasource:\n#    dynamic:\n#      datasource:\n#        master:\n#          driver-class-name: oracle.jdbc.driver.OracleDriver\n#          url: jdbc:oracle:thin:@//127.0.0.1:1521/XE?remarksReporting=true\n#          username: SNOWY\n#          password: 12345678\n#      strict: true\n\n  # mssql\n#  datasource:\n#    dynamic:\n#      datasource:\n#        master:\n#          driver-class-name: com.microsoft.sqlserver.jdbc.SQLServerDriver\n#          url: jdbc:sqlserver://localhost:1433;DatabaseName=SNOWY\n#          username: sa\n#          password: 12345678\n#      strict: true\n\n  # dm database\n#  datasource:\n#    dynamic:\n#      datasource:\n#        master:\n#          driver-class-name: dm.jdbc.driver.DmDriver\n#          url: jdbc:dm://localhost:5236/SYSDBA\n#          username: SYSDBA\n#          password: SYSDBA\n#      strict: true\n\n  # kingbase database\n#  datasource:\n#    dynamic:\n#      datasource:\n#        master:\n#          driver-class-name: com.kingbase8.Driver\n#          url: jdbc:kingbase8://localhost:54321/snowy\n#          username: SYSTEM\n#          password: 123456\n#      strict: true\n\n      # druid global configuration\n      public-key: MFwwDQYJKoZIhvcNAQEBBQADSwAwSAJBAMWiTVtdXFVrgFHDDKELZM0SywkWY3KjugN90eY5Sogon1j8Y0ClPF7nx3FuE7pAeBKiv7ChIS0vvx/59WUpKmUCAwEAAQ==\n      druid:\n        initial-size: 5\n        max-active: 20\n        min-idle: 5\n        max-wait: 60000\n        pool-prepared-statements: true\n        max-pool-prepared-statement-per-connection-size: 20\n        validation-query-timeout: 2000\n        test-on-borrow: false\n        test-on-return: false\n        test-while-idle: true\n        time-between-e